import React, {Component} from 'react';
import './product.css';
import Axios from "axios";
import SingleProduct from "./SingleProduct";
import Navigation from "./Navigation";
import BottomNavigation from "./BottomNavigation";
import Maincontent from "../maincontent/Maincontent";
import Management from "../maincontent/Management";
import ShopByBrand from "../Shop/ShopByBrand/ShopByBrand";

class AllProduct extends Component {

    constructor() {
        super();
        this.state={
            glassproducts:[]
        }
    }



    componentDidMount() {

        Axios.get("http://localhost:2001/evaly/api/product/cat/Glasses")
            .then(res=>{

                this.setState({
                    glassproducts:res.data
                })

            })
            .catch(error=>{
                console.log(error)
            })

    }

    deleteglass=(id)=>{
            let newProductList=this.state.glassproducts.filter(product=>{
                return product._id!=id
            })

        this.setState({
            glassproducts:newProductList
        })




    }












    render() {

        return (
            <div className="allproducts">

                <Navigation/>
                <BottomNavigation/>
                <Management/>
                {/*<ShopByBrand headingName="Shop By Brand"/>*/}

                {



                    this.state.glassproducts.map(value=>{
                        return  <ShopByBrand headingName="Glasses" product={value} deleteMethod={this.deleteglass.bind(this)}/>
                    })


                }



                {/*<ShopByBrand headingName="Shop by Stores"/>*/}




                {/*{*/}
                {/*    this.state.products.map(value=>{*/}
                {/*      return  <SingleProduct product={value} deleteMethod={this.deleteFromStorage.bind(this)}/>*/}
                {/*    })*/}
                {/*}*/}
            </div>
        );
    }
}

export default AllProduct;
