import React, {Component} from 'react';
import './product.css';
import Axios from "axios";
import SingleProduct from "./SingleProduct";
import Navigation from "./Navigation";

class AllProduct extends Component {

    constructor() {
        super();
        this.state={
            products:[]
        }
    }



    componentDidMount() {

        Axios.get("http://localhost:2001/evaly/api/product/")
            .then(res=>{

                this.setState({
                    products:res.data
                })

                {
                    console.log(this.state.products[0].name)
                }

            })
            .catch(error=>{
                console.log(error)
            })

    }

    deleteFromStorage=(id)=>{
            let newProductList=this.state.products.filter(product=>{
                return product._id!=id
            })

        this.setState({
            products:newProductList
        })




    }












    render() {

        // const datalist=this.state.products
        //
        //
        // const selected= datalist.map(dataList=>{
        //     return <option>{dataList.name}</option>
        // })
        // return (
        //
        //     <div>
        //         <select>{selected}</select>
        //     </div>
        // );

        return (
            <div>
                <Navigation/>

                {
                    this.state.products.map(value=>{
                      return  <SingleProduct product={value} deleteMethod={this.deleteFromStorage.bind(this)}/>
                    })
                }
            </div>
        );
    }
}

export default AllProduct;
