import React, { Component } from 'react';
import {connect} from 'react-redux'
import { loadTransactions} from '../store/action/transactionAction'

class Dashboard extends Component {
  
  state={
      createModalOpen:false
  }
  
  
    componentDidMount(){
        this.props.loadTransactions()
        
    }





    render() {
        let {auth,transactions}=this.props
        return (
            <div>
                  <div className="row">

                    <div className="col-md-8 offset-md-2">

                        
                        <h1>Welcome {auth.user.name}</h1>
                         <p>Your Email is {auth.user.email}</p>
                         <button>Create</button>
                <br/>
                <hr/>
                <h1>Transactions:</h1>
                <ul className="list-group">
                    {
                       transactions.map(transacion=>(
                          
                           <li key={transacion._id}
                            className="list-group-item">
                               <p>Type:{transacion.type}</p>
                               <p>Amount:{transacion.amount}</p>

                            </li>
                       )) 
                    }
                </ul>



                    </div>


                  </div>
            </div>
        );
    }
}

const mapStateToProps=state=>({
    auth:state.auth,
    transactions:state.transactions
})




export default connect(mapStateToProps, {loadTransactions})(Dashboard)
