import React, { Component } from 'react';

import './css/singleproduct.css'
import Axios from 'axios'



class ShowSingleProduct extends Component {

    constructor() {
        super();
        this.state = {
            products: [],
            image:"",
            img:[]
        }
    }



    componentDidMount(){
        let productid = this.props.match.params.productid
        console.log(productid)
        Axios.get(`http://localhost:2001/evaly/api/product/${productid}`)
            .then(res=>{
                this.setState({
                    products:res.data,
                    image:res.data.image[0]
                })

                for (var i=0; i<res.data.image.length; i++){
                    console.log()
                }
                console.log(this.state.image)
                
            })
            .catch(error=>{
                console.log(error)
                alert(error)
            })    
    }








    render() {
        return (
            <div className="single-product">
                <div className="single-product-left">
                    <img src={this.state.image} alt="asfd" />
                </div>
                <div className="single-product-right">
        <h1>{this.state.products.image}</h1>

                </div>
            </div>
        );
    }
}

export default ShowSingleProduct;
