import React, {Component} from 'react';

import './addproduct.css';
import  Axios from 'axios'



class AddProduct extends Component {


    constructor() {
        super();

        this.state={
            productname:"",
            discription:"",
            price:"",
            category:"",
            image:null,
            img:""

        }
    }

    changeHandeler=(event)=>{

        this.setState({
            [event.target.name]:event.target.value
        })


    }


    imageHandeler=(event)=>{
            this.setState({
                image:event.target.files[0],
                img:event.target.files[0]
            })
    }

    submitHandeler=(event)=>{
        event.preventDefault()

        let form_data=new FormData()
        form_data.append('image',this.state.image,this.state.image.name)
        form_data.append('name',this.state.productname)
        form_data.append('price',this.state.price)
        form_data.append('category',this.state.category)
        form_data.append('dis',this.state.discription)


        var today = new Date();
        var date = today.getFullYear()+'-'+(today.getMonth()+1)+'-'+today.getDate();



        form_data.append('date',date)
        form_data.append('time',4578454)

        let url='http://localhost:2001/evaly/api/product'

        Axios.post(url,form_data,{
            headers:{
                'content-type':'multipart/form-data'
            }
        }).then(res=>{
            alert(res.data.message)
            console.log(res.data)
            this.setState({
                productname:"",
                discription:"",
                price:"",
                category:"",
                image:null,
                img:""
            })
        }).catch(err=>{
            console.log(err)
        })










    }







    render() {
        return (
            <form onSubmit={this.submitHandeler}>
                    <label htmlFor="productname">Product Name:</label><br/>
                        <input
                            type="text"
                            id="productname"
                            name="productname"
                            onChange={this.changeHandeler}
                            value={this.state.productname}
                        /><br/>
                <label htmlFor="discription">Product Discription:</label><br/>

                <input
                            type="text"
                            id="discription"
                            name="discription"
                            onChange={this.changeHandeler}
                            value={this.state.discription}
                        />
                        <br/>
                <label htmlFor="price">Product Price:</label><br/>

                <input
                            type="text"
                            id="price"
                            name="price"
                            onChange={this.changeHandeler}
                            value={this.state.price}
                        /><br/>
          <label htmlFor="category">Product Category:</label><br/>

                <input
                            type="text"
                            id="category"
                            name="category"
                            onChange={this.changeHandeler}
                            value={this.state.category}
                        /><br/>
                        <label htmlFor="image">Product Image:</label><br/>

                    <input
                            type="file"
                            id="image"
                            name="image"
                            onChange={this.imageHandeler}
                       />
                       <img src={"http://localhost:3000/"+this.state.img.name}/>

                <button onSubmit={this.submitHandeler} className="btn btn-primary d-block my-3">Register</button>





            </form>
        );
    }
}

export default AddProduct;
