import React, {Component} from 'react';
import './product.css';
import Axios from "axios";

class AllProduct extends Component {

    constructor() {
        super();
        this.state={
            products:[]
        }
    }



    componentDidMount() {

        Axios.get("http://localhost:2001/evaly/api/product/")
            .then(res=>{

                this.setState({
                    products:res.data
                })



            })
            .catch(error=>{
                console.log(error)
            })

    }


    render() {


        return (
            <div className="productList">

                {
                    console.log(this.state.products[0].name)
                }

                <img src="http://localhost:2001/2020-09-23T05-15-40.160ZCapture.JPG"/>
                <p className="productname">{this.state.products[0].name}</p>
                <p className="discription">{this.state.products[1].dis}</p>
                <p className="price">{this.state.products[0].price}</p>




            </div>
        );
    }
}

export default AllProduct;
