import React, { Component } from 'react';
import {Link} from "react-router-dom"
import {connect} from "react-redux"
import {register} from "../store/action/authAction"

class Register extends Component {
    //name ,email,password,confrimpassword
    state={
        name:"",
        email:"",
        password:"",
        confrimpassword:"",
        error:{}
    }
    changeHandeler=(event)=>{
        this.setState({
            [event.target.name]:event.target.value
        })
    }

    onSubmitHandeler=(event)=>{
        event.preventDefault()

        let {name, email, password, confrimpassword} = this.state 
        this.props.register({
            name,
            email,
            password,
            confrimpassword
        })
    }




    render() {
        let {name,email,password,confrimpassword,error}=this.state 
        return (
            <div className="row">
                <div className="col-md-6 offset-md-3">
                    <h1 className="text-center display-4">Register</h1>
                    <form onSubmit={this.onSubmitHandeler}>

                        <div className="form-group">
                            <label htmlFor="name">Name:</label>
                            <input type="text"
                                    className={error.name ? 'from-control invalid-feedback':'from-control'}
                                    placeholder="Enter Your Name"
                                    name="name"
                                    id="name"
                                    value={name}
                                    onChange={this.changeHandeler}
                            />


                        </div>
                        
                        <div className="form-group">
                            <label htmlFor="email">Email:</label>
                            <input type="text"
                                className={error.email ? 'from-control invalid-feedback' : 'from-control'}
                                    placeholder="Enter Your Email"
                                    name="email"
                                    id="email"
                                    value={email}
                                    onChange={this.changeHandeler}
                            />
                        </div> 
                        
                        
                        
                        
                        <div className="form-group">
                            <label htmlFor="password">Password:</label>
                            <input type="password"
                                className={error.password ? 'from-control invalid-feedback' : 'from-control'}
                                    placeholder="Enter Your Password"
                                    name="password"
                                    id="password"
                                    value={password}
                                    onChange={this.changeHandeler}
                            />
                        </div> 
                        
                        <div className="form-group">
                            <label htmlFor="confirmPassword">Confirm Password:</label>
                            <input type="password"
                                className={error.confrimpassword ? 'from-control invalid-feedback' : 'from-control'}
                                    placeholder="Confirm Your Password"
                                   name="confrimpassword"
                                    id="confirmPassword"
                                    value={confrimpassword}
                                    onChange={this.changeHandeler}
                            />
                        </div>

                        <Link to="/login">Already Have Account? Login here</Link>
                        <button onSubmit={this.onSubmitHandeler} className="btn btn-primary d-block my-3">Register</button>

                    </form>

                </div>
            </div>
        );
    }
}

export default connect(null,{register})( Register);
