import React, {Component} from 'react';
import './product.css';
import Axios from "axios";

class AllProduct extends Component {

    constructor() {
        super();
        this.state={
            products:[]
        }
    }



    componentDidMount() {

        Axios.get("http://localhost:2001/evaly/api/product/")
            .then(res=>{

                this.setState({
                    products:res.data
                })
            })
            .catch(error=>{
                console.log(error)
            })

    }


    render() {
    const productLIst=this.state.products
       const SingleDataList= productLIst.map(singleProduct=>{
            return <React.Fragment>

                <img src={singleProduct.image}/>
                <p className="productname">{singleProduct.name}</p>
                <p className="discription">{singleProduct.discription}</p>
                <p className="price">{singleProduct.price}</p>

            </React.Fragment>
        })


        return (
            <div className="productList">


                {SingleDataList}



            </div>
        );
    }
}

export default AllProduct;
