import React, { Component } from 'react';
import Modal from 'react-modal'
import { connect } from 'react-redux'
import { updateTransaction } from '../../store/action/transactionAction'


const customStyles = {
    content: {
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        marginRight: '-50%',
        transform: 'translate(-50%, -50%)',
        width: '500px'
    }
}

class UpdateTransaction extends Component {

    state = {

        amount: 0,
        note: ''
    }
    componentDidMount(){
        this.setState({
            amount:this.props.transaction.amount,
            note:this.props.transaction.note

        })
    }



    changeHandeler = (event) => {

        this.setState({
            [event.target.name]: event.target.value
        })
    }

    submitHandler = event => {
        event.preventDefault()
         this.props.updateTransaction(this.props.transaction._id,this.state)
        // this.props.close
       
    }

    render() {
        let { amount, note } = this.state
        return (
            <Modal isOpen={this.props.isOpen}
                onRequestClose={this.props.close}
                style={customStyles}
                contentLabel="Update This Transaction"
            >
                <h2>Create A New Transaction</h2>
                <form onSubmit={this.submitHandler}>
                    <div className="form-group">
                        <label htmlFor="amount">Amount:</label>
                        <input type="number"
                            className="form-control"
                            placeholder="Enter Amount"
                            name="amount"
                            id="amount"
                            value={amount}
                            onChange={this.changeHandeler}
                        />

                    </div>

                    <div className="form-group">
                        <label htmlFor="note">Note:</label>
                        <textarea
                            className="form-control"
                            placeholder="Enter A Note"
                            name="note"
                            id="note"
                            value={note}
                            onChange={this.changeHandeler}
                        />

                    </div>
                    <button className="btn btn-primary">Update</button>
                </form>
            </Modal>
        );
    }
}

export default connect(null, { updateTransaction })(UpdateTransaction);
