import React, { Component } from 'react';
import {Link} from 'react-router-dom'

import {login} from '../store/action/authAction'
import {connect} from 'react-redux'

class Login extends Component {
    //name ,email,password,confrimpassword
    state = {
        email: "",
        password: "",
        error: {}
    }




    static getDerivedStateFromProps(nextProps, prevState) {
        if (JSON.stringify(nextProps.auth.error) != JSON.stringify(prevState.error)) {
            return {
                error: nextProps.auth.error
            }
        }
        return null
    }












    changeHandeler = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    onSubmitHandeler = (event) => {
        event.preventDefault()
        this.props.login({
            email:this.state.email,
            password:this.state.password
        },this.props.history)



    }




    render() {
        let {email, password, error } = this.state
        return (
            <div className="row">
                <div className="col-md-6 offset-md-3">
                    <h1 className="text-center display-4">Login Here</h1>
                    <form onSubmit={this.onSubmitHandeler}>

                        <div className="form-group">
                            <label htmlFor="email">Email:</label>
                            <input type="text"
                                className={error.email ? 'form-control is-invalid' : 'form-control'}
                                   placeholder="Enter Your Email"
                                name="email"
                                id="email"
                                value={email}
                                onChange={this.changeHandeler}
                            />
                            <div className="invalid-feedback">
                                {error.email}
                            </div>
                        </div>




                        <div className="form-group">
                            <label htmlFor="password">Password:</label>
                            <input type="password"
                                className={error.password ? 'form-control is-invalid' : 'form-control'}
                                placeholder="Enter Your Password"
                                name="password"
                                id="password"
                                value={password}
                                onChange={this.changeHandeler}
                            />
                            <div className="invalid-feedback">
                                {error.password}
                            </div>
                        </div>
                        <Link to="/register" >Don't Have Account? Register here</Link>
                        <button onSubmit={this.onSubmitHandeler} className="btn btn-primary d-block my-3">Login</button>

                    </form>

                </div>
            </div>
        );
    }
}

const mapStateToProps=state=>({
    auth:state.auth


})


export default connect(mapStateToProps, { login })(Login) ;
