import * as Types from './types'
import Axios from 'axios'
import { response } from 'express'

export const loadTransactions = () => dispatch =>{
    Axios.get('http://localhost:2000/api/transactions/')
        .then(response=>{
            console.log(response.data)
            dispatch({
                type:Types.LOAD_TRANSACTIONS,
            payload:{
                transactions: response.data
            }
            })
        })
        .catch(error=>{
            console.log(error)
        })
}


export const addNewTransaction=(transaction)=>dispatch=>{
    Axios.post('http://localhost:2000/api/transactions/',transaction)
        .then(res=>{
            dispatch({type:Types.CREATE_TRANSACTION,payload:{
                transaction:res.data
            }})
        })
        .catch(error=>{
            console.log(error)
        })
}


