import React, {Component} from 'react';

import './addproduct.css';



class AddProduct extends Component {


    constructor() {
        super();

        this.state={
            productname:"",
            discription:"",
            price:"",
            category:"",
            image:null

        }
    }

    changeHandeler=(event)=>{

        this.setState({
            [event.target.name]:event.target.value
        })


    }










    render() {
        return (
            <div className="">
                    <label htmlFor="productname">Product Name:</label><br/>
                        <input
                            type="text"
                            id="productname"
                            name="productname"
                            onChange={this.changeHandeler}
                            value={this.state.productname}
                        /><br/>
                <label htmlFor="discription">Product Discription:</label><br/>

                <input
                            type="text"
                            id="discription"
                            name="discription"
                            onChange={this.changeHandeler}
                            value={this.state.discription}
                        />
                        <br/>
                <label htmlFor="price">Product Price:</label><br/>

                <input
                            type="text"
                            id="price"
                            name="price"
                            onChange={this.changeHandeler}
                            value={this.state.price}
                        /><br/>
          <label htmlFor="category">Product Category:</label><br/>

                <input
                            type="text"
                            id="category"
                            name="category"
                            onChange={this.changeHandeler}
                            value={this.state.category}
                        /><br/>
                        <label htmlFor="image">Product Image:</label><br/>

                    <input
                            type="file"
                            id="image"
                            name="image"
                            onChange={this.imageHandeler}
                            value={this.state.image}
                        />






            </div>
        );
    }
}

export default AddProduct;
