import React, { Component } from 'react';
import {connect} from 'react-redux'
import { loadTransactions, removeTransaction} from '../store/action/transactionAction'
import CreateTransaction from '../components/Transactions/createTransactions'
import UpdateTransaction from '../components/Transactions/updateTransaction'


class Dashboard extends Component {
  
  state={
      createModalOpen:false,
      updateModalOpen:false,
      id:""
  }
  openCreateModal=()=>
  {
      this.setState({
          createModalOpen:true
      })
  } 
   closeCreateModal=()=>
  {
      this.setState({
          createModalOpen:false
      })
  } 
   openUpdateModal=(id)=>
  {
      this.setState({
          updateModalOpen:true,
          id
      })
  } 
   closeUpdateModal=()=>
  {
      this.setState({
          updateModalOpen:false,
          id:''
      })
  }
  
  
    componentDidMount(){
        this.props.loadTransactions()
        
    }





    render() {
        let {auth,transactions}=this.props
        return (
            <div>
                  <div className="row">

                    <div className="col-md-8 offset-md-2">

                        
                        <h1>Welcome {auth.user.name}</h1>
                         <p>Your Email is {auth.user.email}</p>
                         <button className="btn btn-primary" onClick={this.openCreateModal}>Create New Transaction</button>
                        <CreateTransaction 
                            isOpen={this.state.createModalOpen}
                            close={this.closeCreateModal}
                        
                        />
                <br/>
                <hr/>
                <h1>Transactions:</h1>
                <ul className="list-group">
                    {
                       transactions.map(transacion=>(
                          
                           <li key={transacion._id}
                            className="list-group-item">
                               <p>Type:{transacion.type}</p>
                               <p>Amount:{transacion.amount}</p>
                               <p>Note:{transacion.note}</p>

                            {
                                   this.state.id == transacion._id?
                                       <UpdateTransaction
                                           isOpen={this.state.updateModalOpen}
                                           close={this.closeUpdateModal}
                                           transaction={transacion}

                                       />:null
                                   
                            }



                                <button
                                className="btn  btn-danger"
                                   onClick={() => this.props.removeTransaction(transacion._id)}
                                >Remove
                                </button> 
                                
                                    <button
                                className="btn  btn-success"
                                   onClick={() => this.openUpdateModal(transacion._id)}
                                >Update
                                </button>
                            </li>
                       )) 
                    }
                </ul>



                    </div>


                  </div>
            </div>
        );
    }
}

const mapStateToProps=state=>({
    auth:state.auth,
    transactions:state.transactions
})




export default connect(mapStateToProps, { loadTransactions, removeTransaction})(Dashboard)
