import React, {Component} from 'react';
import './product.css';
import Axios from "axios";
import SingleProduct from "./SingleProduct";
import Navigation from "./Navigation";
import BottomNavigation from "./BottomNavigation";
import Maincontent from "../maincontent/Maincontent";
import Management from "../maincontent/Management";
import ShopByBrand from "../Shop/ShopByBrand/ShopByBrand";
import ShopByCategory from "../Shop/ShopByBrand/ShopByCategory";
import CategoryHeading from "../Shop/ShopByBrand/CategoryHeading";

class AllProduct extends Component {

    constructor() {
        super();
        this.state={
            products:[],
            glasses:[],
            computers:[]
        }
    }



    componentDidMount() {
        let glass="Glasses"
        let computer="Computer"

        Axios.get(`http://localhost:2001/evaly/api/product/`)
            .then(res=>{


                this.setState({
                    products:res.data
                })

                {
                    console.log(this.state.products)
                }

            })
            .catch(error=>{
                console.log(error)
            })


        //get all glasses


        Axios.get(`http://localhost:2001/evaly/api/product/cat/${glass}`)
            .then(res=>{

                this.setState({
                    glasses:res.data
                })
            })

        //get all computers


        Axios.get(`http://localhost:2001/evaly/api/product/cat/${computer}`)
            .then(res=>{

                this.setState({
                    computers:res.data
                })
            })




    }

    deleteFromStorage=(id)=>{
            let newProductList=this.state.products.filter(product=>{
                return product._id!=id
            })

        this.setState({
            products:newProductList
        })




    }












    render() {

        return (
            <div className="allproducts">

                <Navigation/>
                <BottomNavigation/>
                <Management/>
                <ShopByBrand headingName="Shop By Brand"/>
                <ShopByBrand headingName="Shop by Stores"/>




                {/*{*/}
                {/*    this.state.products.map(value=>{*/}
                {/*      return  <SingleProduct product={value} deleteMethod={this.deleteFromStorage.bind(this)}/>*/}
                {/*    })*/}
                {/*}*/}


                <CategoryHeading name="Glasses"/>

                {

                    this.state.glasses.map(glass=>{
                        return <ShopByCategory  product={glass} />
                    })
                }
                <br/>

                <CategoryHeading name="Computers"/>

                {

                    this.state.computers.map(computer=>{
                        return <ShopByCategory  product={computer} />
                    })
                }




            </div>
        );
    }
}

export default AllProduct;
