import React, {Component} from 'react';
import  './manageproduct.css'
import AddProduct from "./AddProduct";
import Axios from "axios";
import SingleProduct from "./SingleProduct";

class ManageProduct extends Component {

    constructor() {
        super();
        this.state={
            products:[]
        }
    }



    componentDidMount() {

        Axios.get("http://localhost:2001/evaly/api/product/")
            .then(res=>{

                this.setState({
                    products:res.data
                })

                {
                    console.log(this.state.products[0].name)
                }

            })
            .catch(error=>{
                console.log(error)
            })

    }

    deleteFromStorage=(id)=>{
        let newProductList=this.state.products.filter(product=>{
            return product._id!=id
        })

        this.setState({
            products:newProductList
        })




    }










    render() {
        return (
            <div className="manageMain">
                    <div className="add">
                            <AddProduct/>
                    </div>
                    <div className="showProduct">

                        {
                            this.state.products.map(value=>{
                                return  <SingleProduct product={value} deleteMethod={this.deleteFromStorage.bind(this)}/>
                            })
                        }
                    </div>

            </div>
        );
    }
}

export default ManageProduct;
