import React, {Component} from 'react';

import './addproduct.css';
import  Axios from 'axios'



class AddProduct extends Component {


    constructor() {
        super();

        this.state={
            productname:"",
            discription:"",
            price:"",
            category:"",
            image:null

        }
    }

    changeHandeler=(event)=>{

        this.setState({
            [event.target.name]:event.target.value
        })


    }


    imageHandeler=(event)=>{
            this.setState({
                image:event.target.files[0]
            })
    }

    submitHandeler=(event)=>{
        event.preventDefault()


        let data={
            image:this.state.image,
            name:this.state.image,
            price:this.state.price,
            category:this.state.category,
            dis:this.state.discription,
            date:"15-10-2020",
            time:"12.10"
        }

        // let form_data=new FormData()
        // form_data.append('image',this.state.image,this.state.image.name)
        // form_data.append('name',this.state.productname)
        // form_data.append('price',this.state.price)
        // form_data.append('category',this.state.category)
        // form_data.append('discription',this.state.discription)

        let url='http://localhost:2001/evaly/api/product'


        console.log(data)

        //
        // Axios.post(url,form_data,{
        //     headers:{
        //         'content-type':'multipart/form-data'
        //     }
        // }).then(res=>{
        //     console.log(res.data)
        // }).catch(err=>{
        //     console.log(err)
        // })










    }







    render() {
        return (
            <form onSubmit={this.submitHandeler}>
                    <label htmlFor="productname">Product Name:</label><br/>
                        <input
                            type="text"
                            id="productname"
                            name="productname"
                            onChange={this.changeHandeler}
                            value={this.state.productname}
                        /><br/>
                <label htmlFor="discription">Product Discription:</label><br/>

                <input
                            type="text"
                            id="discription"
                            name="discription"
                            onChange={this.changeHandeler}
                            value={this.state.discription}
                        />
                        <br/>
                <label htmlFor="price">Product Price:</label><br/>

                <input
                            type="text"
                            id="price"
                            name="price"
                            onChange={this.changeHandeler}
                            value={this.state.price}
                        /><br/>
          <label htmlFor="category">Product Category:</label><br/>

                <input
                            type="text"
                            id="category"
                            name="category"
                            onChange={this.changeHandeler}
                            value={this.state.category}
                        /><br/>
                        <label htmlFor="image">Product Image:</label><br/>

                    <input
                            type="file"
                            id="image"
                            name="image"
                            onChange={this.imageHandeler}
                       />

                <button onSubmit={this.submitHandeler} className="btn btn-primary d-block my-3">Register</button>





            </form>
        );
    }
}

export default AddProduct;
