import React, {Component} from 'react';
import './product.css';
import Axios from "axios";
import SingleProduct from "./SingleProduct";

class AllProduct extends Component {

    constructor() {
        super();
        this.state={
            products:[]
        }
    }



    componentDidMount() {

        Axios.get("http://localhost:2001/evaly/api/product/")
            .then(res=>{

                this.setState({
                    products:res.data
                })

                {
                    console.log(this.state.products[0].name)
                }

            })
            .catch(error=>{
                console.log(error)
            })

    }


    render() {

        const datalist=this.state.products


        const selected= datalist.map(dataList=>{
            return <option>{dataList.name}</option>
        })
        return (

            <div>
                <select>{selected}</select>
            </div>
        );

        return (
            <div className="productList">

                {
                    this.state.products.map(value=>{
                        <SingleProduct name={value.name}/>
                    })
                }

                {/*<img src="http://localhost:2001/2020-09-23T05-15-40.160ZCapture.JPG"/>*/}
                {/*<p className="productname">{this.state.products[0].name}</p>*/}
                {/*<p className="discription">{this.state.products[1].dis}</p>*/}
                {/*<p className="price">{this.state.products[0].price}</p>*/}




            </div>
        );
    }
}

export default AllProduct;
