import React, {Component} from 'react';
import './product.css';
import Axios from "axios";




class SingleProduct extends Component {

onDelete=(id)=>{



    Axios.delete(`http://localhost:2001/evaly/api/product/${id}`)
        .then(res=>{
            this.props.deleteMethod(id)

        })


}

    render() {
        return (
                     <div className="productList">
                           <img src={"http://localhost:2001/"+this.props.product.image}/>
                            <button className="delete-button" onClick={this.onDelete.bind(this,this.props.product._id)}>Delete</button>

                         <p className="productname">{this.props.product.name}</p>
                         <p className="discription">{this.props.product.dis.substr(0, 25)+"....."}</p>
                         <p className="price">{this.props.product.price}</p>


                    </div>

        );
    }
}

export default SingleProduct;
